// Get the incoming data from the webhook node
const data = $input.first().json.body;

// Check if the body exists and is an object
if (!data || typeof data !== 'object') {
  throw new Error('Missing or invalid webhook body data.');
}

// Basic validation for required fields
if (!data.email || !data.name) {
  throw new Error('Missing required fields: name and email');
}

// Sanitize and clean the data
const cleanData = {
  name: data.name.trim(),
  email: data.email.toLowerCase().trim(),
  phone: data.phone?.replace(/[^\d+\-\s]/g, '') || '',
  company: data.company?.trim() || 'Unknown',
  website: data.website?.trim() || '',
  industry: data.industry || 'General',
  interest: data.interest || 'General',
  message: data.message?.trim() || '',
  budget: data.budget || 'Not specified',
  submissionDate: new Date().toISOString()
};

return [{ json: cleanData }];

const data = $input.first().json;
let score = 0;

// Budget scoring
const budgetScores = {
  'Under 1000': 10,
  '1000-5000': 30,
  '5000-10000': 60,
  '10000+': 80
};
score += budgetScores[data.budget] || 20;

// Interest scoring
const interestScores = {
  'Web Development': 40,
  'E-commerce': 50,
  'Mobile App': 45,
  'Consulting': 30
};
score += interestScores[data.interest] || 25;

// Company presence scoring
if (data.company !== 'Unknown') score += 15;
if (data.phone) score += 10;

// Message length scoring (engagement indicator)
if (data.message.length > 50) score += 15;
if (data.message.length > 100) score += 10;

return { 
  json: {
    ...data,
    preliminaryScore: Math.min(score, 100),
    timestamp: new Date().toISOString()
  }
};

