const leadData = $input.first().json;
const aiResponse = $input.last().json;

// Parse AI response
let aiAnalysis;
try {
  aiAnalysis = JSON.parse(aiResponse.message.content);
} catch (e) {
  // Fallback if JSON parsing fails
  aiAnalysis = {
    sentiment: 'neutral',
    intent: 'sales',
    qualification: 'warm',
    urgency: 'medium',
    summary: 'Analysis pending',
    suggestedTags: ['general'],
    adjustedScore: leadData.preliminaryScore
  };
}

// Merge lead data with AI analysis
const finalData = {
  ...leadData,
  ...aiAnalysis,
  processedAt: new Date().toISOString()
};

return { json: finalData };

// Log successful processing
const data = $input.first().json;
console.log(`Lead processed: ${data.name} - ${data.qualification} (Score: ${data.adjustedScore})`);

// Send notification to admin if high-value lead
if (data.adjustedScore > 80) {
  console.log(`🔥 HIGH-VALUE LEAD ALERT: ${data.name} scored ${data.adjustedScore}/100`);

  return {
    json: {
      ...data,
      notification: true,
      alertMessage: `🔥 High-value lead detected: ${data.name} (${data.adjustedScore}/100)`,
      status: 'high-priority-processed'
    }
  };
}

return { 
  json: {
    ...data,
    notification: false,
    status: 'processed'
  }
};

// Tag as spam and log
const data = $input.first().json;

console.log(`⚠️ SPAM DETECTED: ${data.name} - ${data.email}`);

// Update contact with spam flag
return {
  json: {
    ...data,
    tags: [...(data.suggestedTags || []), 'spam', 'do-not-contact'],
    status: 'spam-flagged',
    processNote: 'Automatically flagged as spam by AI'
  }
};

