const input = $input.first().json;

// Check if data is present
if (!input || !input.email) {
  throw new Error('Email verification data is missing or incomplete.');
}

// Access the Hunter.io data directly from the single input item
const hunterData = input;

// Start with the lead's preliminary score.
// If the previous node (Initial Lead Scoring) passes its data to the Merge node,
// this will be handled there. For now, we'll assume a baseline.
// This node's job is just to add the Hunter.io score.
let finalScore = hunterData.score || 0;
let finalReason = `Hunter.io score: ${finalScore}.`;

// Add points for bad or risky data
if (hunterData.disposable) {
  finalScore -= 100;
  finalReason += ' | Disposable email detected.';
} else if (hunterData.status === 'risky' || hunterData.accept_all) {
  finalScore -= 20;
  finalReason += ' | Risky email status.';
} else if (hunterData.webmail) {
  finalScore -= 5;
  finalReason += ' | Webmail address.';
}

// Return only the data that this node is responsible for
// The Merge node will combine this with data from other branches
return [{ json: {
  hunterScore: finalScore,
  hunterReason: finalReason
}}];

const input = $input.first().json;
const scrapedData = input.data;

// Initialize a default status
let isLegit = false;
let reason = 'Did not meet criteria';
let score = 0;

// Access the markdown and metadata
const markdownContent = scrapedData.markdown;
const metadata = scrapedData.metadata;

// Check 1: Strong Business Indicators
if (markdownContent.includes('Pricing plans') || markdownContent.includes('Key Features') || markdownContent.includes('Testimonials')) {
  score += 50;
  reason = 'Includes key business sections';
}

// Check 2: Professional Metadata
if (metadata.title && metadata.description) {
  score += 50;
  reason = `${reason}, with professional metadata`;
}

// Final check based on a score threshold
if (score >= 50) {
  isLegit = true;
}

// Return the original data plus the new legitimacy fields and score
const output = {
  ...input, // <-- This line is crucial. It passes all the original data through.
  isLegit: isLegit,
  legitReason: reason,
  legitScore: score
};

return [{ json: output }];
